<?phpclass NHP_Options_heading_type_select extends NHP_Options{			/**	 * Field Constructor.	 *	 * Required - must call the parent constructor, then assign field and value to vars, and obviously call the render field function	 *	 * @since NHP_Options 1.0	*/	function __construct($field = array(), $value ='', $parent){				parent::__construct($parent->sections, $parent->args, $parent->extra_tabs);		$this->field = $field;		$this->value = $value;										}//function		/**	 * Field Render Function.	 *	 * Takes the vars and outputs the HTML for the field in the settings	 *	 * @since NHP_Options 1.0	*/	function render(){		$class = (isset($this->field['class']))?'class="'.$this->field['class'].'" ':'';				echo '<p class="description" style="color:red;">'.__('The fonts style provided below are free to use custom fonts from the Font Style').'</p>';		$head =array('h1','h2','h3','h4','h5','h6');		echo '<select id="'.$this->field['id'].'" name="'.$this->args['opt_name'].'['.$this->field['id'].']" '.$class.'rows="6" >';		echo '<option value="'.$fonts->style.':'.$head.'" '.selected($this->value, $fonts->style.':'.$head, false).'>Default</option>';			foreach($head as $heading){						echo '<option value="'.$fonts->style.':'.$heading.'" '.selected($this->value, $fonts->style.':'.$heading, false).'>'.$fonts->style.'  '.$heading.'</option>';			}				echo '</select>';		echo (isset($this->field['desc']) && !empty($this->field['desc']))?' <span class="description">'.$this->field['desc'].'</span>':'';	}//function	}//class?>