<?php/*** Template Name: Single Col Fixed Width*/if ( ! defined( 'ABSPATH' ) ) exit; // Exit if accessed directly get_header(); ?><section id="container" >     <div class="page-header fused-single-header">        <div class="container">            <div class="row row-headlines">                 <hgroup>                     <h1><?php blogfused_the_headline();?></h1>                 </hgroup>                </div>            <div class="row breadcrumbs">                <?php breadcrumb_trail(blogfused_get_breadcrumbs_args()); ?>            </div>                                    </div> <!-- end of button row -->            </div><!--end of page header --> <div class="container">     <div class='row-fluid'>        <div id="content" class="span8 center">           <?php while ( have_posts() ) : the_post(); ?>              <article <?php post_class('clearfix');?> id="post-<?php the_ID();?>">                <?php  if ( has_post_thumbnail() ) : ?>                        <div class="post-format-img">                            <?php the_post_thumbnail('post-thumbnail-fix'); ?>                        </div>                 <?php endif; ?>                <div class="entry">                    <?php the_content();?>                </div>               </article>                <?php  blogfused_get_comment_template();?>           <?php endwhile; // end of the loop. ?>        </div>     </div> </div> <!-- container class for fixed width -->     </section>     <?php get_footer();?>